                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Render current month's page */}
          <MonthPage
            month={currentMonth}
            year={currentYear}
            transactions={allMonthsTransactions[`${currentYear}-${currentMonth}`] || []}
            onUpdateTransaction={addTransaction}
            onDeleteTransaction={deleteTransaction}
          />
        </>
      ) : (
        <CreditCardTracker />
      )}
    </div>
  );
}

export default App;
