      } else {
        // Update one-time transaction
        setAllMonthsTransactions(prev => ({
          ...prev,
          [monthKey]: prev[monthKey].map(t =>
            t.id === id ? { ...t, ...editingValues } : t
          )
        }));
      }
    } else {
      // Handle regular transactions
      setTransactions(transactions.map(t => 
        t.id === id ? { ...t, ...editingValues } : t
      ));
